mapper.select:
SELECT 1 AS DEBT_FLAG, ins.SETTLE_TYPE AS SETTLE_TYPE, ins.REPAY_PLAN_NO AS REPAY_PLAN_NO, 0 AS ASSEMBLY_ID, NULL AS PAYOFF_DATE, ins.CREATE_CORE_SYSTE_DATE AS ACCT_DATE, ast.ORGID AS ORG_CODE, ast.ASSET_LOAN_ACCOUNT AS LOAN_ACCOUNT, ast.ASSET_DEBT_NO AS DEBT_NO, ast.ASSET_BORROWER_IDNUM AS BORROWER_IDNUM, 0 AS PAYOFF_FLAG, cii.CARDII_NAME AS LOAN_NAME, ast.ASSET_ITEM_NO AS ASSET_NO FROM ( SELECT t.ASSET_ITEM_NO, MAX( t.REPAY_PLAN_NO ) AS REPAY_PLAN_NO, ANY_VALUE ( t.SETTLE_TYPE ) AS SETTLE_TYPE, ANY_VALUE ( t.CREATE_CORE_SYSTE_DATE ) AS CREATE_CORE_SYSTE_DATE FROM QN_DB_BIZ.INTEREST_SETTLE t, QN_DB_BIZ.REPAY_PLAN r WHERE t.REPAY_PLAN_NO = r.REPAY_PLAN_NO AND r.REPAY_PLAN_DUE_AT <= TO_DATE(#{e.timeSegment.startDate}, 'yyyy-MM-dd' ) AND t.CREATE_CORE_SYSTE_DATE = TO_DATE(#{e.timeSegment.startDate}, 'yyyy-MM-dd' ) GROUP BY t.ASSET_ITEM_NO ) ins, QN_DB_BIZ.ASSET ast, QN_DB_BIZ.CARDII cii
ins.ASSET_ITEM_NO = ast.ASSET_ITEM_NO AND ast.ASSET_BORROWER_IDNUM = cii.CARDII_IDNUM
AND ast.ORGID in (${e.orgCode})
AND cii.CARDII_NAME = #{e.loanName}
AND ast.ASSET_BORROWER_IDNUM = #{e.borrowerIdnum}
AND ast.ASSET_LOAN_ACCOUNT = #{e.loanAccount}
AND 0 = #{e.payoffFlag}
AND ins.CREATE_CORE_SYSTE_DATE BETWEEN #{e.timeSegment.startDate} AND #{e.timeSegment.endDate}
union all
SELECT 2 AS DEBT_FLAG, NULL AS SETTLE_TYPE, NULL AS REPAY_PLAN_NO, rar.REPAY_ASSEMBLY_ID AS ASSEMBLY_ID, rar.REPAY_FINISH_TIME AS PAYOFF_DATE, rar.CORE_SYS_DATE AS ACCT_DATE, ast.ORGID AS ORG_CODE, ast.ASSET_LOAN_ACCOUNT AS LOAN_ACCOUNT, ast.ASSET_DEBT_NO AS DEBT_NO, ast.ASSET_BORROWER_IDNUM AS BORROWER_IDNUM, CASE his.ASSET_STATUS_AF WHEN TO_NCHAR('payoff') THEN 2 ELSE CASE rar.REPAY_RESULT WHEN TO_NCHAR('Full') THEN 1 ELSE 0 END END AS PAYOFF_FLAG, cii.CARDII_NAME AS LOAN_NAME, ast.ASSET_ITEM_NO AS ASSET_NO FROM QN_DB_BIZ.REPAY_ASSEMBLY_RECORD rar, QN_DB_BIZ.ASSET ast, QN_DB_BIZ.CARDII cii, QN_DB_BIZ.ASSET_HISTORY his
rar.ASSET_NO = ast.ASSET_ITEM_NO AND ast.ASSET_BORROWER_IDNUM = cii.CARDII_IDNUM AND rar.ASSET_HISTORY_ID = his.ASSET_HISTORY_ID
AND ast.ORGID in (#{e.orgCode})
AND cii.CARDII_NAME = #{e.loanName}
AND ast.ASSET_BORROWER_IDNUM = #{e.borrowerIdnum}
AND ast.ASSET_LOAN_ACCOUNT = #{e.loanAccount}
AND rar.REPAY_RESULT != 'Full' AND his.ASSET_STATUS_AF != 'payoff'
AND rar.REPAY_RESULT = 'Full' AND his.ASSET_STATUS_AF != 'payoff'
AND his.ASSET_STATUS_AF = 'payoff'
AND rar.CORE_SYS_DATE BETWEEN #{e.timeSegment.startDate} AND #{e.timeSegment.endDate}
order by ASSET_NO asc, ACCT_DATE asc, PAYOFF_DATE nulls first
