mapper.select:
SELECT 0 AS PAYOFF_PRINCIPAL, 0 AS PAYOFF_INTEREST, 0 AS AMOUNT, 0 AS ASSEMBLY_ID, NULL AS PAYOFF_DATE, grt.ASSET_GRANT_RECORD_CREATE_AT AS VALID_DATE, grt.ASSET_GRANT_RECORD_AMOUNT AS GTANT_AMOUNT, grt.ASSET_GRANT_RECORD_AMOUNT AS PRINCIPAL_BALANCE, grt.CORE_SYS_DATE AS ACCT_DATE, 1 AS DEBT_FLAG, ast.ASSET_ITEM_NO AS ASSET_NO, ast.ORGID AS ORG_CODE, ast.ASSET_ACTUAL_GRANT_AT AS GRANT_DATE, ast.ASSET_DEBT_NO AS DEBT_NO, ast.ASSET_LOAN_ACCOUNT AS LOAN_ACCOUNT, ast.ASSET_BORROWER_IDNUM AS BORROWER_IDNUM, cii.CARDII_NAME AS LOAN_NAME FROM QN_DB_BIZ.ASSET_GRANT_RECORD grt, QN_DB_BIZ.ASSET ast, QN_DB_BIZ.CARDII cii
grt.ASSET_GRANT_RECORD_ASSET_ITEM_NO = ast.ASSET_ITEM_NO AND ast.ASSET_BORROWER_IDNUM = cii.CARDII_IDNUM
AND ast.ASSET_BORROWER_IDNUM = #{e.borrowerIdnum}
AND ast.ORGID = #{e.orgCode}
AND cii.CARDII_NAME = #{e.loanName}
AND ast.ASSET_LOAN_ACCOUNT = #{e.loanAccount}
AND (ast.ASSET_ACTUAL_GRANT_AT BETWEEN #{e.timeSegment.startDate} AND #{e.timeSegment.endDate})
UNION ALL SELECT 0 AS PAYOFF_PRINCIPAL, 0 AS PAYOFF_INTEREST, 0 AS AMOUNT, rar.REPAY_ASSEMBLY_ID AS ASSEMBLY_ID, rar.REPAY_FINISH_TIME AS PAYOFF_DATE, rar.CORE_SYS_DATE AS VALID_DATE, ast.ASSET_GRANTED_PRINCIPAL_AMOUNT AS GTANT_AMOUNT, 0 AS PRINCIPAL_BALANCE, rar.CORE_SYS_DATE AS ACCT_DATE, 1 AS DEBT_FLAG, ast.ASSET_ITEM_NO AS ASSET_NO, ast.ORGID AS ORG_CODE, ast.ASSET_ACTUAL_GRANT_AT AS GRANT_DATE, ast.ASSET_DEBT_NO AS DEBT_NO, ast.ASSET_LOAN_ACCOUNT AS LOAN_ACCOUNT, ast.ASSET_BORROWER_IDNUM AS BORROWER_IDNUM, cii.CARDII_NAME AS LOAN_NAME FROM QN_DB_BIZ.REPAY_ASSEMBLY_RECORD rar, QN_DB_BIZ.ASSET ast, QN_DB_BIZ.CARDII cii
rar.ASSET_NO = ast.ASSET_ITEM_NO AND ast.ASSET_BORROWER_IDNUM = cii.CARDII_IDNUM
AND ast.ASSET_BORROWER_IDNUM = #{e.borrowerIdnum}
AND ast.ORGID in #{e.orgCode}
AND cii.CARDII_NAME = #{e.loanName}
AND ast.ASSET_LOAN_ACCOUNT = #{e.loanAccount}
AND ((ast.ASSET_ACTUAL_GRANT_AT BETWEEN #{e.timeSegment.startDate} AND #{e.timeSegment.endDate}) or (rar.REPAY_FINISH_TIME BETWEEN #{e.timeSegment.startDate} AND #{e.timeSegment.endDate}))
ORDER BY ASSET_NO ASC, PAYOFF_DATE ASC NULLS first
